import React from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

let ProjectModal = (props) => {
  let project = props.project;
  return (
    <Modal
      {...props}
      size="xl"
      aria-labelledby="contained-modal-title-vcenter"
      // centered
      scrollable
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter" style={{display: 'inline-block'}}>
          {project.project_name}
        </Modal.Title>

        <div className="ml-auto">
          <Button href={project.live_link} target="_blank" variant="outline-success">Live Demo</Button>
          <Button href={project.github_link} target="_blank" variant="outline-dark">Github Repo</Button>
        </div>

      </Modal.Header>

      <Modal.Body>
        <img src={project.image_urls[0]} alt="project 1" />
        <img src={project.image_urls[1]} alt="project 2" />
        <p>
          {project.description}
        </p>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={props.onHide}>Close</Button>
      </Modal.Footer>
    </Modal>
  );
}

export default ProjectModal;
